const e=[{id:"mercury",distance:80,speed:.24},{id:"venus",distance:110,speed:.615},{id:"earth",distance:140,speed:1},{id:"mars",distance:170,speed:.53},{id:"jupiter",distance:220,speed:.084},{id:"saturn",distance:260,speed:.034},{id:"uranus",distance:300,speed:.012},{id:"neptune",distance:340,speed:1.006}],d=()=>{let s=1e-4*Date.now();e.forEach(e=>{let d=document.getElementById(e.id),t=s*e.speed,i=e.distance*Math.cos(t)+300,n=e.distance*Math.sin(t)+300;d.style.left=`${i}px`,d.style.top=`${n}px`}),requestAnimationFrame(d)};d();